type BookKind = 'books#volumes';

type IndustryIdentifiers = {
  type: 'OTHER';
  identifier: string;
};

export type Book = {
  kind: '#books#volume';
  id: string;
  etag: string;
  selfLink: string;
  volumeInfo: {
    title: string;
    subtitle?: string;
    authors: string[];
    publishedDate: string;
    industryIdentifiers: IndustryIdentifiers[];
    redingModes: {
      text: boolean;
      image: boolean;
    };
    description?: string;
    pageCount: number;
    printType: 'BOOK';
    categories: string[];
    maturityRating: 'NOT_MATURE' | 'MATURE';
    allowAnonLoggin: boolean;
    contentVersion: string;
    panelizationSummary: {
      containsEpubBulles: boolean;
      containsImageBubbles: boolean;
    };
    imageLinks: {
      smallThumbnail: string;
      thumbnail: string;
    };
    language: keyof isoLangs;
    previewLink: string;
    infoLink: string;
    canonicalVolumeLink: string;
  };
  salesInfo: {
    country: keyof Countries;
    saleability: 'NOT_FOR_SALE' | 'FOR_SALE';
    isEbook: boolean;
  };
  accessInfo: {
    country: keyof Countries;
    viewability: 'NO_PAGES' | 'PAGES';
    embeddable: boolean;
    publicDomain: boolean;
    textToSpeechPermission: 'ALLOWED' | 'NOT_ALLOWED';
  };
  epub: {
    isAvailable: boolean;
  };
  pdf: {
    isAvailable: boolean;
  };
  webReaderLink: string;
  accessViewStatus: 'NONE';
  quoteSharingAllowed: boolean;
  searchSnippet: string;
};

export type GoogleBooksApi = {
  kind: BookKind;
  totalItems: number;
  items: Book[];
};
